package com.pranay.interview.arrays;

public class CheckPalindromeFormation {
    public static void main(String[] args) {
        String a = "ulacfd", b = "jizalu";
//        String a = "";
//        String b = "";
        boolean ans = checkPalindromeFormation(a, b);
        System.out.println("Ans : " + ans);
    }

    public static boolean checkPalindromeFormation(String a, String b) {
        for (int i = 0; i <= a.length() / 2; i++) {
            String apre = a.substring(0, i);
            String asuf = a.substring(i);
            String bpre = b.substring(0, i);
            String bsuf = b.substring(i);

            int n = a.length();
            String apre1 = a.substring(0, n - i);
            String asuf1 = a.substring(n - i);
            String bpre1 = b.substring(0, n - i);
            String bsuf1 = b.substring(n - i);


            System.out.println(apre + " - " + asuf);
            System.out.println(bpre + " - " + bsuf);

            System.out.println(apre1 + " - " + asuf1);
            System.out.println(bpre1 + " - " + bsuf1);

            System.out.println("-----------");
            boolean ans1 = checkPalindrome(apre + bsuf);
            boolean ans2 = checkPalindrome(bpre + asuf);

            boolean ans3 = checkPalindrome(apre1 + bsuf1);
            boolean ans4 = checkPalindrome(bpre1 + asuf1);


            if (ans1 || ans2)
                return true;

            if (ans3 || ans4)
                return true;
        }

        return false;
    }

    private static boolean checkPalindrome(String word) {
        word = word.toLowerCase();
        StringBuffer sb = new StringBuffer(word);
        String reverse = sb.reverse().toString();
        if (word.equals(reverse))
            return true;
        return false;
    }
}
